import * as React from "react";
import styled from "styled-components";
import Deleteimg from "../../../../asset/images/Deleteimg.png";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import { Alert, Snackbar } from "@mui/material";
import axiosConfig from "../../../../api/configapi";

export default function DeleteTask(props) {
  // const [data, setData] = React.useState([]);
  // console.log("data", data);
  const [open, setOpen] = React.useState(false);

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const [openSnackbar, setOpenSnackbar] = React.useState(false);
  const handleOpenSnackbar = () => {
    setOpen(false);
    setOpenSnackbar(true);
  };
  const handleSnackBarClose = () => {
    setOpenSnackbar(false);
  };
  const [success, setSuccess] = React.useState(true);
  const handleDelete = (id) => {
    axiosConfig.delete(`/services/app/Task/Delete?Id=${id}`).catch(() => {
      setSuccess(false);
      console.log(success);
    });
    handleOpenSnackbar();
  };

  //

  const Title = styled.div``;
  return (
    <div>
      <Snackbar
        open={openSnackbar}
        anchorOrigin={{ vertical: "bottom", horizontal: "right" }}
        onClose={handleSnackBarClose}
        autoHideDuration={2000}
      >
        {success ? (
          <Alert
            variant="filled"
            severity="success"
            onClose={handleSnackBarClose}
          >
            {`Delete Success ${props.task.name}`}
          </Alert>
        ) : (
          <Alert
            variant="filled"
            severity="error"
            onClose={handleSnackBarClose}
          >
            {`Task ${props.task.id} is in a project. You can't delete task`}
          </Alert>
        )}
      </Snackbar>
      {props.disabled ? (
        <Button
          style={{ background: "#fb483a", color: "#5a5858" }}
          variant="outlined"
          disabled
          onClick={handleClickOpen}
        >
          Delete
        </Button>
      ) : (
        <Button
          style={{ background: "red", color: "#fff" }}
          variant="outlined"
          onClick={handleClickOpen}
        >
          Delete
        </Button>
      )}
      {/* <Button
        style={{ background: "#fb483a", color: "#fff" }}
        variant="outlined"
        onClick={handleClickOpen}
      >
        Delete
      </Button> */}
      <Dialog
        open={open}
        onClose={handleClose}
        aria-labelledby="alert-dialog-title"
        aria-describedby="alert-dialog-description"
      >
        <DialogTitle id="alert-dialog-title">
          <img
            alt=""
            style={{ height: "200px", marginLeft: "60px" }}
            src={`${Deleteimg}`}
          />
        </DialogTitle>

        <DialogContent sx={{ width: "400px", textAlign: "center" }}>
          <DialogContentText id="alert-dialog-description">
            <b style={{ fontSize: "30px" }}>Are you sure?</b>
            <Title>Delete project: "{props.task.name}"?</Title>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClose}>No</Button>
          <Button
            style={{ backgroundColor: "#7cd1f9", color: "#fff" }}
            onClick={() => handleDelete(props.task.id)}
            autoFocus
          >
            Yes
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
