import React, { useEffect, useState } from "react";
import styled from "styled-components";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableRow from "@mui/material/TableRow";
import { useDispatch, useSelector } from "react-redux";
import {
  resetProgress,
  taskSelector,
} from "../../../../redux/reducer/taskReducer";
import EditTasks from "../editTask/editTask";
import {
  archiveTask,
  deArchiveTask,
  deleteTask,
} from "../../../../redux/actions/task";
import { RootState } from "../../../../redux/store";

const Title = styled.div`
  font-size: 16px;
  font-weight: 600;
  line-height: 30px;
`;

const TitleContent = styled.div`
  font-size: 14px;
`;

const Common = styled.div`
  padding: 10px 25px;
  gap: 50px;
`;

const TableCommon = styled.div``;

const BtnTask = styled.button`
  font-size: 14px;
  background: #fff;
  border-radius: 5px;
  border: 1px solid;
  padding: 8px 15px;
  :hover {
    cursor: pointer;
  }
`;

const BtnTaskDelete = styled.button`
  font-size: 14px;
  background: #e9e9e9;
  border-radius: 5px;
  border: 1px solid;
  padding: 8px 15px;
`;

const ItemRight = styled.div`
  display: flex;
  gap: 10px;
`;

const CommonTask = () => {
  const [open, setOpen] = React.useState(true);

  const handleClick = () => {
    setOpen(!open);
  };
  

  return (
    <Common>
      <Title>Common Task ({9})</Title>
      <TitleContent>
        These tasks are automatically added to all new projects
      </TitleContent>
      <hr />
      <TableCommon>
        <Title>Name</Title>
        
       
          return (
            <Table
              aria-label="simple table"
              sx={{ border: 1, color: "#e9e9e9" }}
              key={1}
            >
              <TableBody>
                <TableRow
                  sx={{ "&:last-child td, &:last-child th": { border: 0 } }}
                >
                  <TableCell scope="row" sx={{ width: "5px", padding: "0" }}>
                    <EditTasks/>
                  </TableCell>
                  <TableCell scope="row"></TableCell>
                  <TableCell
                    align="right"
                    sx={{ width: "5px", padding: "5px 5px" }}
                    onClick={handleClick}
                  >
                   
                      <ItemRight>
                        <BtnTask
                          onClick={() => {
                            // onArchive(item.id);
                          }}
                        >
                          Archive
                        </BtnTask>
                        <BtnTaskDelete
                          style={{ background: "#0000001f" }}
                          disabled
                        >
                          Delete
                        </BtnTaskDelete>
                      </ItemRight>
                    ) : (
                      <ItemRight>
                        <BtnTask
                          onClick={() => {
                            // onDeArchive(item.id);
                          }}
                        >
                          Unarchive
                        </BtnTask>
                        <BtnTaskDelete
                          style={{ background: "#ffffff", cursor: "pointer" }}
                          onClick={() => {
                            // onDelete(item.id);
                          }}
                        >
                          Delete
                        </BtnTaskDelete>
                      </ItemRight>
                    )
                  </TableCell>
                </TableRow>
              </TableBody>
            </Table>
      </TableCommon>
    </Common>
  );
};

export default CommonTask;
