import React from "react";
import { MoreVert } from "@mui/icons-material";
import styled from "styled-components";
import CommonTask from "./component/commontask/CommonTask";
import OtherTask from "./component/OtherTack/OtherTack";
import CreateTask from "./component/CreateTask/CreateTask";
import SearchTask from "./component/SearchTask/SearchTask";
import { useState } from "react";

const TaskContainer = styled.div`
  width: 100%;
  height: 100%;
  /* background: black; */
  border: 20px solid #ccc;
  box-sizing: border-box;
  /* margin-left: 300px;
  margin-top: 100px; */
  /* margin: 100px 0 0 300px; */
  width: 70%;
`;

const TaskContent = styled.div`
  padding: 10px 10px 10px 10px;
`;
const HeaderTask = styled.div`
  display: flex;
  justify-content: space-between;
  padding: 15px;
`;

const TitleHeader = styled.div`
  font-size: 22px;
`;
const AddContent = styled.div`
  display: flex;
`;

const Task = () => {
  const [name, setName] = useState();
  const [refreshUi, setRefreshUi] = useState();
  return (
    <TaskContainer>
      <TaskContent>
        <HeaderTask>
          <TitleHeader>Manage Tasks</TitleHeader>
          <MoreVert />
        </HeaderTask>
        <hr />
        <AddContent>
          <CreateTask setRefreshUi />
          <SearchTask setName={setName} />
        </AddContent>
        <CommonTask name={name} />
        <OtherTask name={name} />
      </TaskContent>
      {console.log(refreshUi)}
    </TaskContainer>
  );
};

export default Task;
