import * as React from "react";
import "./CommonTask.scss";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { Button } from "@mui/material";
import EditTasks from "../EditTask/EditTask";
import EditTask from "../EditTask/EditTask";
import axios, { Axios } from "axios";

const data = [
  { id: 1, title: "html" },
  { id: 2, title: "css" },
  { id: 3, title: "Javascrip" },
  { id: 4, title: "React" },
];

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

// const baseURL = " https://my.api.mockaroo.com/data.json?key=a718cfd0";

const CommonTask = () => {
  const [list, setList] = React.useState([]);
  const [isLoading, setIsLoading] = React.useState(true);

  React.useEffect(() => {
    const getPosts = async () => {
      const response = await axios.get(
        " https://my.api.mockaroo.com/data.json?key=a718cfd0"
      );

      setList(response.data);
      setIsLoading(false);
    };

    getPosts();
  }, [list]);

  if (!list) return null;

  const handleDelete = (id) => {
    setList(list.filter((item) => item.id !== id));
    console.log(id);
  };

  return (
    <div className="contener" style={{ width: "100%" }}>
      <div className="title">
        <div className="title1">Common Task ({list.length})</div>
        <div className="title2">
          These tasks are automatically added to all new projects
        </div>
        <hr style={{ color: "rgb(247, 40, 40)" }} />
        <h2>Name</h2>
      </div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 500 }} aria-label="customized table">
          <TableBody>
            {list.map((row) => (
              <StyledTableRow key={row.id}>
                <StyledTableCell
                  style={{ width: "420px", textAlign: "center" }}
                  component="th"
                  scope="row"
                >
                  {row.title}
                </StyledTableCell>
                <StyledTableCell align="right" style={{ float: "right" }}>
                  <div className="row-item">
                    <div className="itemRight">
                      <EditTask />
                    </div>
                    <div className="itemRight">
                      <Button
                        style={{
                          width: "20px",
                          background: "white",
                          color: "black",
                          padding: "5px",
                        }}
                        variant="contained"
                      >
                        Archive
                      </Button>
                    </div>
                    <div className="itemRight">
                      <Button
                        style={{ width: "20px", background: "red" }}
                        variant="contained"
                        // disabled
                        onClick={() => handleDelete(row.id)}
                      >
                        Delete
                      </Button>
                    </div>
                  </div>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default CommonTask;
