import * as React from "react";
import Button from "@mui/material/Button";
import Dialog from "@mui/material/Dialog";
import DialogActions from "@mui/material/DialogActions";
import DialogContent from "@mui/material/DialogContent";
import DialogContentText from "@mui/material/DialogContentText";
import DialogTitle from "@mui/material/DialogTitle";
import useMediaQuery from "@mui/material/useMediaQuery";
import { useTheme } from "@mui/material/styles";
import { NativeSelect, TextField } from "@mui/material";
import { useForm } from "react-hook-form";

export default function EditTask(props) {
  const [open, setOpen] = React.useState(false);
  const [editName, setEditName] = React.useState(props.task.name);
  const [edieSelect, setEditSelect] = React.useState(0);
  const theme = useTheme();
  const fullScreen = useMediaQuery(theme.breakpoints.down("md"));

  const handleClickOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  const handleEditList = (id) => {
    console.log(id);

    setOpen(false);
  };

  const { handleSubmit, register } = useForm();
  return (
    <div style={{ width: "500px" }}>
      <Button variant="outlined" onClick={handleClickOpen}>
        Edit
      </Button>
      <Dialog
        fullScreen={fullScreen}
        open={open}
        onClose={handleClose}
        aria-labelledby="responsive-dialog-title"
      >
        <DialogTitle id="responsive-dialog-title">{"Edit Task"}</DialogTitle>
        <DialogContent sx={{ width: "300px" }}>
          <DialogContentText>
            <TextField
              label="Name *"
              variant="standard"
              style={{ width: "100%" }}
              value={editName}
              onChange={(e) => {
                setEditName(e.target.value);
              }}
            />
          </DialogContentText>
        </DialogContent>
        <DialogContent>
          <DialogContentText>
            <NativeSelect
              style={{ width: "100%" }}
              onChange={(e) => {
                setEditSelect(e.target.value);
              }}
            >
              <option value={0}>Common Task</option>
              <option value={1}>Other Task</option>
            </NativeSelect>
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button autoFocus onClick={handleClose}>
            Cancel
          </Button>
          <Button onClick={handleEditList} autoFocus>
            Save
          </Button>
        </DialogActions>
      </Dialog>
    </div>
  );
}
