import React from "react";
import { Route, Switch, useRouteMatch } from "react-router-dom";
import style from "style-component";
import Tasks from '../../pages/task/task';
import NotFound from '../NotFound/NotFound';
import Root from '../../routes/Root';
import Projects from "../../pages/project/project";


export const MainContent = style.div`
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
`;

export const Content = style.div`
    width: 95%;
    height: 95%;
    background: #fff;
    border: 30px solid #e9e9e9;
`;

const Main = () => {
  const { path } = useRouteMatch();
  return (
    <MainContent>
      <Content>
        <Switch>
          <Route exact path={path} component={NotFound}></Route>
          <Root
            path={`${path}/home`}
            component={NotFound}
            exact={false} />
          <Root
            path={`${path}/main/tasks`}
            component={Tasks}
            exact={false} />
          {/* <Root
                path={`${path}/main/projects`}
                component={Projects}
                exact={false}
            /> */}
        </Switch>
      </Content>
    </MainContent>
  );
}

export default Main;
