import * as React from "react";
import "CommonTask.scss";
// import styled from "styled-components";
import { styled } from "@mui/material/styles";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell, { tableCellClasses } from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { Button } from "@mui/material";

// const CommenTaskContainer = styled.div``;

// const TitleTask = styled.div``;
// const Title1 = styled.div``;
// const Title2 = styled.div``;

const data = [
  { id: 1, title: "html" },
  { id: 2, title: "css" },
  { id: 3, title: "Javascrip" },
];

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  [`&.${tableCellClasses.head}`]: {
    backgroundColor: theme.palette.common.black,
    color: theme.palette.common.white,
  },
  [`&.${tableCellClasses.body}`]: {
    fontSize: 14,
  },
}));

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  "&:nth-of-type(odd)": {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  "&:last-child td, &:last-child th": {
    border: 0,
  },
}));

const CommonTask = () => {
  const [value, setValue] = React.useState(data);

  const handleDelete = (i) => {
    setValue(value.filter((item, idText) => idText !== i));
  };

  return (
    <div className="contener" style={{ width: "100%" }}>
      <div className="title">
        <div className="title1">Common Task (96)</div>
        <div className="title2">
          These tasks are automatically added to all new projects
        </div>
        <hr />
        <h2>Name</h2>
      </div>
      <TableContainer component={Paper}>
        <Table sx={{ minWidth: 500 }} aria-label="customized table">
          <TableBody>
            {data.map((row) => (
              <StyledTableRow key={row.id}>
                <StyledTableCell
                  style={{ width: "420px", textAlign: "center" }}
                  component="th"
                  scope="row"
                >
                  {row.title}
                </StyledTableCell>
                <StyledTableCell align="right" style={{ float: "right" }}>
                  <Button style={{ width: "20px" }} variant="contained">
                    edit
                  </Button>
                </StyledTableCell>
                <StyledTableCell align="right">
                  <Button
                    style={{
                      width: "20px",
                      background: "white",
                      color: "black",
                    }}
                    variant="contained"
                  >
                    Archive
                  </Button>
                </StyledTableCell>
                <StyledTableCell align="right">
                  <Button
                    style={{ width: "20px", background: "red" }}
                    variant="contained"
                    onClick={() => handleDelete()}
                  >
                    Delete
                  </Button>
                </StyledTableCell>
              </StyledTableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
    </div>
  );
};

export default CommonTask;
