import React, { useEffect } from "react";
import styled from "styled-components";
import { ITask } from "../../interface";
import { Button } from "@material-ui/core";
import EditTask from "../EditTask/EditTask";
import { useDispatch, useSelector } from "react-redux";
import { archiveTask, getAllTasks, getCommonTasks } from "../../TaskSlice";

//styled
const CommonTaskContainer = styled.div`
  & table {
    width: 100%;
    color: #555555;
    
    & th {
     
      font-size: 14px;
      text-align: left;
      padding: 10px;
      border-bottom: 2px solid #f3f3f3;
    }
    & td {
      margin: 10px;
      padding: 10px;
      border-bottom: 2px solid #f3f1f1;
     
    }
    
    & tr {
      background-color: #f6f6f6;
      border-bottom: 2px solid #f2f2f2;
      &:nth-child(even) {
        background: white;
      }
      &:hover{
        background:#f1f1f1;
      }
    }
  }
`;

const ActionBtn = styled.div`
  float:right;
`;

const CommonTask = () => {


  
  const dispatch=useDispatch();

  //filter to Common Tasks

  const commonTask = useSelector(getCommonTasks);
  
  const handleDeArchive = (id:number) =>{
    dispatch(archiveTask(id));
  }


  return (
    <CommonTaskContainer>
      <h5>Common Task ({commonTask.length}) </h5>
      <p>These tasks are automatically added to all new projects</p>
      <table>
        <thead>
          <tr>
            <th colSpan={3}>Name</th>
          </tr>
        </thead>

        <tbody>
          {commonTask.map((item) => {
            return (
              <tr key={commonTask.indexOf(item)}>
                <td style={{width:"5%"}} >
                  {/* <EditTask value={item} /> */}
                  </td>
                  <td>
                  {item.name}
                  {/* Check isDeleted of Task */}
                  {!item.isDeleted ? (
                    <ActionBtn>
                      <Button
                        style={{ marginRight: "10px" ,background:'#FFFFFF', textTransform: 'none' }}
                        variant="contained"
                        
                      >
                        Archive
                      </Button>
                      <Button variant="contained" style={{ textTransform: 'none' }}  disabled>
                        Delete
                      </Button>
                    </ActionBtn>
                  ) : (
                    <ActionBtn>
                      <Button 
                      onClick={()=>handleDeArchive(item.id)}

                        value={item.id}
                        variant="contained"
                        style={{ marginRight: "10px", background:'#FFFFFF', textTransform: 'none' }}
                      >
                        UnArchive
                      </Button>
                      <Button
                      variant="contained"
                        style={{ background:'#FFFFFF', textTransform: 'none' }}
                        // onClick={() => handleDelete(item.id)}
                      >
                        Delete
                      </Button>
                    </ActionBtn>
                  )}
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </CommonTaskContainer>
  );
};

export default CommonTask;
