import React, { useEffect, useState } from 'react'
import styled from 'styled-components'
import PersonIcon from '@mui/icons-material/Person';
import HttpsIcon from '@mui/icons-material/Https';
import { Alert, Box, Button, TextField } from '@mui/material';
import { useNavigate } from 'react-router';
// import {IFormLoginRequest} from '../../interfaces/type';
import {useForm} from 'react-hook-form'
import axios from 'axios';

const LoginContainer    = styled.div`
  background: #00bcd4;
  width: 100%;
  height: 100vh;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const FormLogin = styled.div`
  width: 30%;
`;

const Titie = styled.div`
  font-size: 36px;
  font-weight: bold;
  color: #fff;
  margin-bottom: 30px;
  text-align: center;
`

const Form = styled.form`
   b{
    text-align: center;
     margin: 0;
     padding: 0;
     font-size: 20px;
   }
   display: flex;
  flex-direction: column;
  background: #fff;
  padding: 50px;
  gap: 20px;
  border-radius: 10px;
  align-content: center;
`

const Input = styled.div`
  display: flex;
  margin-left: 50px;
`

const Login = () => {
  
 
  const { register, handleSubmit } = useForm();
  const navigate = useNavigate();


  const [loginFail,setLoginFail]= useState(false)
   
  const [loginSuccess,setLoginSuccess]= useState(false)

  const handleSubmitLogin = async (dataSubmit)=>{
    console.log('submit')
    axios({
      method: 'post',
      url: 'https://pnm-server.herokuapp.com/signin',
      data: {
        email: dataSubmit.username,

        password: dataSubmit.password
      }
    }).then(
      data=>{if (data?.token) {
        setLoginSuccess(true)}
        setLoginFail(false)
        navigate('/layout')        
      
      })
      .catch(err=>{
       setLoginFail(true) 
      console.log(err)
    });
  }


  return (
    <>
      {loginFail && <Alert severity="error">Username hoặc mật khẩu không đúng</Alert>}
    <LoginContainer>
    
        <FormLogin>
            <Titie>TimeSheet</Titie>
            <Form onSubmit={handleSubmit(handleSubmitLogin)}>
                <b>Login</b>
               <Input>
               {/* <PersonIcon 
               style={{marginTop: "25px",
                      width: "50px",
               }}

               /> <TextField id="standard-basic" label="UserName" variant="standard" 
                {...register("username")}
               /> */}
               <Box sx={{ display: 'flex', alignItems: 'flex-end' }}>
        <PersonIcon sx={{ color: 'action.active', mr: 1, my: 0.5 }} />
        <TextField id="input-with-sx" label="User"  type="password" variant="standard" 
          {...register("username")}
        />
      </Box>
               </Input> 
               <Input>
               <HttpsIcon 
               style={{marginTop: "25px",
                      width: "50px",
               }}

               /><TextField
    name="password"
    type="password"
    placeholder="password"
    label="Password"
   {...register("password")}
               />
               </Input>
                
                <Button
                  type="submit"
                  variant="contained"
                  color="primary"
                  style={{ width: "50%", left: "25%" }}
                  on
                >
                  Login
              </Button>
          </Form>
        </FormLogin>
    </LoginContainer>
    </>
  )
}

export default Login
